import { AmbientLight, PointLight, SpotLight } from 'three';
export const LightsList = [];
const ambientLight = new AmbientLight('rgb(255, 255, 255)', 0.5);
export const pointLight = new PointLight('rgb(240, 225, 240)', 3, 80, 0.1);
pointLight.position.set(20, 20, 20);
export const spotLight = new SpotLight('rgb(255, 255, 255)', 1, 200, Math.PI / 180 * 30, 0, 0);
spotLight.castShadow = true;
spotLight.position.set(100, 50, 100);
LightsList.push(ambientLight, pointLight, spotLight);
