import React, { useEffect, useState } from 'react';
import { TEngine } from "./TEngine/TEngine"
import socket from "./Socket/index"
import './App.css';
function getPlatform() {

  var info = navigator.userAgent;
  //通过正则表达式的test方法判断是否包含“Mobile”字符串
  var isPhone = /mobile/i.test(info);
  //如果包含“Mobile”（是手机设备）则返回true
  return isPhone
}
function App() {
  const [alpha, setAlpha] = useState(0)
  const [beta, setBeta] = useState(0)
  const [gamma, setGamma] = useState(0)
  const [t, setT] = useState({ x: 0, y: 0, z: 0 })
  useEffect(() => {
  }, [])
  useEffect(() => {
    console.log('first')
    let isGun = getPlatform();
    if (isGun) {

      window.addEventListener('deviceorientation', function (event: any) {

        let a = event.alpha;
        let b = event.beta;
        let g = event.gamma;
        setAlpha(a.toFixed(2))
        setBeta(b.toFixed(2))
        setGamma(g.toFixed(2))
        let t={a,b,g}
        socket.emit('orientation',t)
      })
    } else {
      const threeTarget = document.querySelector(".canvas-three")
      const TE = new TEngine(threeTarget as HTMLDivElement)
      TE.loadGun()
      TE.loadRoom()
      socket.on('getdata',(event:any)=>{
        console.log('getdata',event)
        let {a,b,g}=event
        TE.setRotate(a, b, g)
        setAlpha(a.toFixed(2))
        setBeta(b.toFixed(2))
        setGamma(g.toFixed(2))
      })
      window.addEventListener("keydown", (e) => {
        if (e.keyCode === 27) {

          TE.disableControls()

        } else if (e.keyCode === 17) {
          TE.enableControls()
        }
      })
    }




  }, [])

  return (
    <div className="App">

      {/* <div>左右：{
        alpha
      }</div>
      <div>前后：{
        beta
      }</div>
      <div>扭转：{
        gamma
      }</div> */}
      <div className='canvas-three'></div>
      <div>{getPlatform()}</div>
    </div>
  );
}

export default App;
