import { TEngine } from "./TEngine/TEngine"
import { LightsList } from './TEngine/Tlights'

import './App.css';

function App() {
  const [alpha, setAlpha] = useState(0)
  const [beta, setBeta] = useState(0)
  const [gamma, setGamma] = useState(0)
  useEffect(() => {
    var re = /([0-9]+\.[0-9]{2})[0-9]*/;
    window.addEventListener('deviceorientation', function (event: any) {
      console.log(event)
      let a = event.alpha;
      let b = event.beta;
      let g = event.gamma;
      setAlpha(a.toFixed(2))
      setBeta(b.toFixed(2))
      setGamma(g.toFixed(2))

    })
  }, [])
  useEffect(()=>{
    console.log('first')
    const threeTarget = document.querySelector(".canvas-three")
    const TE = new TEngine(threeTarget as HTMLDivElement)
    TE.loadRoom(()=>{})
  },[])
  return (
    <div className="App">

      <div>左右：{
        alpha
      }</div>
      <div>前后：{
        beta
      }</div>
      <div>扭转：{
        gamma
      }</div>
<div className='canvas-three'></div>
    </div>
  );
}

export default App;
