import React, { useEffect, useState } from 'react';
import logo from './logo.svg';
import './App.css';

function App() {
  const [info,setinfo]=useState({alpha:0,gamma:0,beta:0})
  const [alpha,setAlpha]=useState(0)
  const [beta,setBeta]=useState(0)
  const [gamma,setGamma]=useState(0)
  useEffect(() => {
    var re = /([0-9]+\.[0-9]{2})[0-9]*/;
    window.addEventListener('deviceorientation', function (event: any) {
      setinfo(event);
      console.log(event)
      let a = event.alpha;
      let b = event.beta;
      let g = event.gamma;
      setAlpha(a.replace(re,"$1"))
      setBeta(b.replace(re,"$1"))
      setGamma(g.replace(re,"$1"))

    })
  }, [])
  return (
    <div className="App">

<div>沿z轴：{
  alpha
}</div>
<div>沿x轴：{
  beta
}</div>
<div>沿y轴：{
  gamma
}</div>

    </div>
  );
}

export default App;
