import React, { useEffect, useState } from 'react';
import logo from './logo.svg';
import './App.css';

function App() {
  const [info,setinfo]=useState({alpha:0,gamma:0,beta:0})
  useEffect(() => {
    window.addEventListener('deviceorientation', function (event: any) {
      setinfo(event);
      var alpha = event.alpha;
      // var beta = event.beta;
      var gamma = event.gamma;
      var newH = event.alpha / 2 * (-1);
      var newV = event.gamma * 1;
      if (gamma <= 0) {
        newV = 90 + gamma;
        newH = -alpha;
      } else {
        newV = gamma - 90;
        alpha = (alpha + 180) % 360;
        newH = -alpha;
      }

    })
  }, [])
  return (
    <div className="App">

<div>{
  info.alpha
}</div>
<div>{
  info.beta
}</div>
<div>{
  info.gamma
}</div>

    </div>
  );
}

export default App;
